const loginUser=async e=>{console.log(e),e.rememberMe="on"==e.rememberMe;try{const r=await axios.post("http://localhost:3000/api/auth",e);console.log(r)}catch(e){console.error(e)}},registerUser=async e=>{try{const r=await axios.post("http://localhost:3000/api/register",e);console.log(r)}catch(e){console.error(e)}};function onLoad(){const e=document.getElementsByClassName("login-main").item(0).querySelector("form"),r=document.getElementsByClassName("register-main").item(0).querySelector("form");e.addEventListener("submit",r=>{r.preventDefault();const t=e.querySelector("#email").value,o=e.querySelector("#password").value,s=e.getElementsByClassName("flex-align").item(0).querySelector("#remember").value;loginUser({email:t,password:o,rememberMe:s})}),r.addEventListener("submit",e=>{e.preventDefault();const t=r.querySelector("#name").value,o=r.querySelector("#email").value,s=r.querySelector("#password").value;if(s!=r.querySelector("#password-confirm").value)return 403;registerUser({name:t,email:o,password:s})})}